import React, { Component } from 'react';
//import axios from 'axios'
import {Link} from 'react-router-dom'
import { login } from "../../actions/accountlogin";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";

//could be combined with signup
//convert to pure component? 

const Login = ({ handleLoginFormChange, handleChange, handleSubmit, name, password }) => {
  return (
      <div className="Login">
        <h1>Log In</h1>
        <form onSubmit={handleSubmit}>
          <input
            placeholder="name"
            type="text"
            name="name"
            value={name}
            onChange={handleChange}
          /><br/>
        <input
            placeholder="password"
            type="password" //can't see type
            name="password"
            value={password}
            onChange={handleChange}
          /><br/>          
        <button placeholder="submit" type="submit">
            Log In
          </button>          
          <div>
            or <Link to="signup">Sign Up</Link>
          </div>

          </form>
          <div>
          {
            this.state.errors ? this.handleErrors() : null
          }
        </div>
      </div>
  );

}

//function NoMatch() {
  //let location = useLocation();

  //return (
    //<div>
      //<h3>
        //No match for <code>{location.pathname}</code>
      //</h3>
    //</div>
  //);
//}

const mapDispatchToProps = (dispatch) => {
  return {
    accountlogin: (account) => {
      dispatch({
        type: "SET_CURRENT_ACCOUNT",
        account: account,
      });
    },

    login: (credentials, history) => dispatch(login(credentials, history)),
  };
};



export default withRouter(connect(null, mapDispatchToProps)(Login));
