export default function loginFormReducer(state = null, action) {
  switch (action.type) {
    case "SET_CURRENT_ACCOUNT":
     if (action.payload){
        localStorage.setItem(action.payload);
      }
      return {
      ...state,
       account: action.payload
      };
    case "CLEAR_CURRENT_ACCOUNT":
      return null 
    default:
      return state 
    };
}
