		e.persist();
		e.preventDefault();
		props.newDepartment(props.account.account.id, props.history);
	};

	const navigateTo = (id) => () =>
		props.history.push(
			`/accounts/${props.account.account.id}/departments/new`
		);

	return (
		<div class="container">
			<div class="row align-items-center">
				<div class="col">
					{props.accounts
						? props.accounts.map((account) => {
								return (
									<React.Fragment>
										<div key={account.id}>
											<label> Account Name - </label>{" "}
											{account}
											<br></br>
										</div>
										<div key={account.id}>
											<label> Service - </label>{" "}
											{department.attributes.service}
											<br></br>
										</div>
										<div key={account.id}>
											<label> Charge - </label> $
											{department.attributes.charge}
										</div>
										<Button
											onClick={navigateTo(department.id)}
										>
											Make Payment
										</Button>
									</React.Fragment>
								);
						  })
						: null}
				</div>
			</div>
		</div>
	);
};

export default Accounts;
