const initialState = {
  name: '',
  password: ''
};


export default function loginFormReducer(state = initialState, action) {
  switch (action.type) {
    case "UPDATE_LOGIN_FORM":
      return action.formData
    case "CLEAR_CURRENT_USER":
      return initialState
    default:
      return state 
    };
}
