export const newPayment = (form, history) => {
  return (dispatch) => {
    return fetch(
      `http://localhost:3000/api/v1/accounts/${history}/payments/new`,
      {
        method: "POST",
        body: form,
      }
    )
      .then((response) => response.json())
      .then((payment) => {
        if (payment.error) {
          alert("error");
        } else {
          console.log("payment.data is: " + JSON.stringify(payment.data));
          dispatch({ type: "ADD_PAYMENT", payload: payment });
          history.push(`/accounts/${history}`);
        }
      });
  };
};

