import React from 'react'
import {connect} from 'react-redux'
import {deleteDepartment} from '../actions/deleteDepartment'

class Departments extends React.Component {

  render() {

	const handleDelete = (department) => {
      this.props.deleteDepartment(department.id, department.account_id)
	}

          console.log(this.props.departments.data.length)


	return (
        <div>
          <h3>
            <button onClick={() => handleDelete(props.departments)}>Delete Department</button>
              <h2>{ props.departments.data.length  > 1 ? 
               props.departments.data.map(department => (
               <label> Department Id </label>
              (<React.Fragment key={department.id}> 
              <h2>{department.service}</h2> - 
              <h2>{department.charge}</h2>
              </React.Fragment>))) : null }</h2>
            </h3>
        </div>
		)
  }
}

export default connect(null, {deleteDepartment})(Departments)
