import React from "react";
import { useState } from "react";
import { Form } from "react-bootstrap";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { newDepartment } from ".././actions/currentDepartments.js";

const DepartmentNew = (props) => {
  console.log(props.location.state.accountId);

  const [newDepartmentData, setForm] = useState({
    name: "",
    service: "",
    charge: "",
    account_id: props.location.state.accountId, //id of account you are adding the charge to
    //account_id: needs to be the account that you want to add to, not logged in as,
  });

  const handleNewDepartmentFormChange = (event, target) => {
    setForm({
      ...newDepartmentData,
      [target]: event.target.value,
    });
  };

  const handleNewDepartmentFormSubmit = (event) => {
    event.preventDefault();
    props.newDepartment(newDepartmentData, props.account.id);
  };

  return (
    <div className="Department">
      <h1 className="text-center">New Charge</h1>
      <Form onSubmit={handleNewDepartmentFormSubmit}>
        <div className="form-group align-items-center">
          <br></br>
          <br></br>
          <label>Name:</label>
          <input
            type="text"
            className="form-control-sm"
            name="name"
            value={newDepartmentData.name}
            onChange={(event) => handleNewDepartmentFormChange(event, "name")}
          />
          <br></br>
          <br></br>
          <div className="form-group  align-items-center">
            <label>Service:</label>
            <input
              type="text"
              className="form-control-sm"
              name="service"
              value={newDepartmentData.service}
              onChange={(event) =>
                handleNewDepartmentFormChange(event, "service")
              }
            />
          </div>
          <br></br>
          <br></br>
          <div className="form-group  align-items-center">
            <label>Charge:</label>
            <input
              type="text"
              className="form-control-sm"
              name="charge"
              value={newDepartmentData.charge}
              onChange={(event) =>
                handleNewDepartmentFormChange(event, "charge")
              }
            />
          </div>
          <button type="submit" className="btn btn-outline-primary">
            Add Charge
          </button>
        </div>
      </Form>
    </div>
  );
};

/*const mapStateToProps = (state) => {
  return {
    newDepartmentData: state.newDepartmentData,
  };
};*/

export default withRouter(connect(null, { newDepartment })(DepartmentNew));
