  return (
    <ElementsConsumer>
      {({ stripe, elements }) => (
        <CheckoutForm stripe={stripe} elements={elements} />
      )}
    </ElementsConsumer>
  );
}*/

const mapStateToProps = state => {
  return {
    form: state.form 
  };
};

export default withRouter(connect(mapStateToProps, { newPayment } ), injectStripe(PaymentNew));
