import React from "react";
import { connect } from "react-redux";
import { Button } from "react-bootstrap";
import { withRouter } from "react-router-dom";
import { newDepartment } from ".././actions/currentDepartments.js";

//presentational component
const Accounts = (props) => {
	console.log(props);

	const handleClick = (e) => {
		//const { accountId } = e.target;
		//const accountId = props.account.id
		e.persist();
		e.preventDefault();
		props.newDepartment(props.account.account.id);
	};

	const navigateTo = (id) => () =>
		this.props.history.push(`/accounts/${id}/payments/new`);

	return (
		<div class="container">
			<div class="row align-items-center">
				<div class="col">
					{props.accounts
						? props.accounts.map((account) => {
								return (
									<React.Fragment>
										<br></br>
										<br></br>
										<div key={account.id}>
											<label> Account Name - </label>{" "}
											{account.attributes.name}
											<br></br>
										</div>
										<div key={account.id}>
											<label> Balance - </label>{" "}
											{account.attributes.balance}
											<br></br>
										</div>
										<Button onClick={handleClick}>
											Add Charge
										</Button>
									</React.Fragment>
								);
						  })
						: null}
				</div>
			</div>
		</div>
	);
};

export default withRouter(connect(null, { newDepartment })(Accounts));
