import React from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { Button } from "react-bootstrap";
import { deleteDepartment } from ".././actions/currentDepartments.js";
//pure if output only depends on props
class Departments extends React.Component {
  state = { name: "" };

  onChange = (event) => {
    console.log("hello");
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  departmentSelector = () => {
    return this.props.departments.filter(
      (department) => department.attributes.name == this.state.name
    );
  };

  render() {
    departmentSelector();
    //const handleDelete = () => {
    //this.props.deleteDepartment()
    //}
    //const departments = this.props.departments;
    console.log(this.state);
    console.log(this.props.departments);
    const navigateTo = (id) => () =>
      this.props.history.push(`/accounts/${id}/payments/new`);
    //only need one return?  needs unique keys
    return (
      <div class="container">
        <div class="row align-items-center">
          <div class="col">
            <select
              value={this.state.name}
              name="name"
              onChange={this.onChange}
            >
              <option value="radiology">Radiology</option>
              <option value="pediatrics">Pediatrics</option>
            </select>
            <h2>
              <label> Department Information</label>
              {this.props.departments
                ? this.props.departments.map((department) => {
                    console.log(this.props.departments);
                    return (
                      <React.Fragment>
                        <br></br>
                        <div key={department.id}>
                          <label> Department Name - </label>{" "}
                          {department.attributes.name}
                          <br></br>
                          <label> Service - </label>{" "}
                          {department.attributes.service}
                          <br></br>
                          <label> Charge - </label> $
                          {department.attributes.charge}
                        </div>
                        <Button onClick={navigateTo(department.id)}>
                          Make Payment
                        </Button>
                        <br></br>
                      </React.Fragment>
                    );
                  })
                : null}
            </h2>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(connect(null, { deleteDepartment })(Departments));
