import React from 'react'
import {connect} from 'react-redux'
import { deleteDepartment } from ".././actions/currentDepartments.js"

class Departments extends React.Component {

 render(props) {

    const handleDelete = () => {
      this.props.deleteDepartment()
    }

    //const departments = this.props.departments;

    return (
            <div>
            <label> Department </label> 
            {this.props.departments ?
              this.props.departments.map(department => {
                return(
                <React.Fragment>
               <div key={department.id}>{department.name}</div>
               </React.Fragment>
               );
              }) : null }
            </div>
           );
  }
}

const mapStateToProps = state => {
  return {
    departments: state.departmentsReducer 
  }
}

export default connect(mapStateToProps, {deleteDepartment})(Departments)
