import React from 'react'
import { useState } from 'react';
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { currentPayments } from "../actions/currentPayments";
import { ElementsConsumer, CardElement } from "@stripe/react-stripe-js";

//form data available in local state or store? 
//class component, local state holding 
const PaymentNew = () => {

  const [form, setForm] = useState({
    amount: ''
  });

	const handlePaymentFormChange = (event, target) => {
     setForm({
      ...form,
     [target]: event.target.value, 
    });
  }


	const handlePaymentFormSubmit = (event) => {
       event.preventDefault()
       /*const { stripe, elements } = this.props;
  		if (!stripe || !elements) {
    	return;
  		}

  		const card = elements.getElement(CardElement);
  		const result = await stripe.createToken(card);
  			if (result.error) {
    	console.log(result.error.message);
  			} else {
    	console.log(result.token);
    // pass the token to your backend API
  			} */
       //add call to action 
	}

	return (
		<div className="NewPayment">
        <h1>New Payment</h1>
        <form onSubmit={handlePaymentFormSubmit}>
          <input
            placeholder="amount"
            type="text"
            name="amount"
            autoComplete="on"
            value={form.amount}
            onChange={(event)=> handlePaymentFormChange(event, "amount")}
          /><br/>
          <CheckoutWithStripe />
        <button placeholder="submit" type="submit">
            Make Payment
          </button> 
          <br></br>     
          <br></br>             
          <div>
          </div>
          </form>
          <div>
        </div>
      </div>
	)
}

function InjectedCheckoutForm() {
  return (
    <ElementsConsumer>
      {({ stripe, elements }) => (
        <CheckoutForm stripe={stripe} elements={elements} />
      )}
    </ElementsConsumer>
  );
}

const mapStateToProps = state => {
  return {
    form: state.form 
  };
};

export default withRouter(connect(mapStateToProps, { currentPayments } )(PaymentNew));
