import React from "react";
import { connect } from "react-redux";
import { Button } from "react-bootstrap";
import { withRouter } from "react-router-dom";
import { newDepartment } from ".././actions/currentDepartments.js";

//presentational component
const Accounts = (props) => {
	console.log(props);

	/*const handleClick = (e) => {
		//const { accountId } = e.target;
		//const accountId = props.account.id
		e.persist();
		e.preventDefault();
		props.newDepartment(props.account.account.id);
	}; */

	const navigateTo = (id) => () =>
		props.history.push(
			`/accounts/${props.account.account.id}/departments/new`,
			{ accountId: id }
		);

	return (
		<div className="container">
			<div className="row align-items-center">
				<div className="col">
					{props.accounts
						? props.accounts.map((account) => {
								if ((account.attributes.status = "patient")) {
									return (
										<React.Fragment>
											<br></br>
											<br></br>
											<div key={account.id}>
												<label> Account Name - </label>{" "}
												{account.attributes.name}
												<br></br>
												<label> Status - </label>{" "}
												{account.attributes.status}
												<br></br>
												<label> Balance - </label>{" "}
												{account.attributes.balance}
												<br></br>
											</div>
											<Button
												onClick={navigateTo(account.id)}
											>
												Add Charge
											</Button>
										</React.Fragment>
									);
								}
						  })
						: null}
				</div>
			</div>
		</div>
	);
};

export default withRouter(connect(null, { newDepartment })(Accounts));
