import React from 'react'
import { useState } from 'react';
import { setCurrentAccount } from "../../actions/currentAccount";

//form data available in local state or store? 
//class component, local state holding 
const PaymentNew = () => {

  const [form, setForm] = useState({
    amount: ''
  });

	const handlePaymentFormChange = (event, target) => {
     setForm({
      ...form,
     [target]: event.target.value, 
    });
  }


	const handlePaymentFormSubmit = (event) => {
       event.preventDefault()
	}

	return (
		<div className="NewPayment">
        <h1>New Payment</h1>
        <form onSubmit={handlePaymentFormSubmit}>
          <input
            placeholder="amount"
            type="text"
            name="amount"
            autoComplete="on"
            value={form.amount}
            onChange={(event)=> handlePaymentFormChange(event, "amount")}
          /><br/>
        <button placeholder="submit" type="submit">
            Make Payment
          </button>          
          <div>
          </div>
          </form>
          <div>
        </div>
      </div>
	)
}

const mapStateToProps = state => {
  return {
    form: state.form 
  };
};

export default withRouter(connect(mapStateToProps, { login, setCurrentAccount } )(Login));
