import React, { Component } from 'react';
//import { Redirect } from 'react-router-dom'
import axios from 'axios'
//import {BrowserRouter, Switch, Route, useLocation /*, withRouter */} from 'react-router-dom'
import Navbar from 'react-bootstrap/Navbar'
import Login from './components/registrations/Login'
//import Signup from './components/registrations/Signup'


class App extends Component {
  constructor() {
    super()
    this.state = {
      currentUser: null,
      loginForm: {
        name: "",
        password: ""
      }
    }
  }


handleLoginFormChange = event => {
  const {name, value } = event.target
    this.setState({
      loginForm: {
        ...this.state.loginForm,
        [name]: value 
      }
    })
};

handleLoginFormSubmit = event => {
    event.preventDefault()
    const {name, password} = this.state
    let account = {
      name: name,
      password: password
    }
  axios.post('http://localhost:3001/api/v1/login', {account}, {withCredentials: true})
    .then(response => {
      console.log(response)
      if (response.data.logged_in) {
        this.props.handleLogin(response.data);
        //this.redirect()
    //  } else {
    //    this.setState({
    //      errors: response.data.errors
      }
    })
    .catch(error => 
      console.log('api errors:', error));


 //redirect = () => {
 //   this.props.history.push('/')
 // }
}

 handleErrors = () => {
    return (
      <div>
        <ul>
        {this.state.errors.map(error => {
        return <li key={error}>{error}</li>
          })}
        </ul>
      </div>
    )
  };

handleSubmit = (data) => {
  this.setState({
    account: data.account 
  })
  this.props.history.push(`/accounts/${this.props.account.id}`);
};


  render() {
    return (
      <div className="App">
        <Navbar/>
        <Login
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
          name={this.state.loginForm.name}
          password={this.state.LoginForm.password}
          />
      </div>
    );
  }
}

export default App; // only one thing to export can use default
