export default function loginFormReducer(state = null, action) {
  switch (action.type) {
    case "SET_CURRENT_ACCOUNT":
     if (action.payload){
        localStorage.setItem("jwt", action.payload.jwt);
      }
      return action.account 
    case "CLEAR_CURRENT_ACCOUNT":
      return null 
    default:
      return state 
    };
}
